(()=>{document.addEventListener("DOMContentLoaded",function(){var n=new JustGage({id:"energy-current-gauge",value:0,min:0,max:400,title:"Aktueller Stromverbrauch",label:"Watt pro Stunde",valueFontColor:"White"});function r(){fetch("fetch_sensors.php").then(e=>e.json()).then(e=>{console.log("Received data:",e);var t=parseFloat(e.energy_current);isNaN(t)?console.error("Invalid energy current value:",e.energy_current):n.refresh(t),document.getElementById("energy-total").innerHTML="Heute im Office verbraucht: "+e.energy_total+" KW",document.getElementById("energy-current").innerHTML="Aktueller Stromverbrauch im Office: "+e.energy_current+" Watt pro Stunde",document.getElementById("temperature-value").innerHTML=e.temperature_15+" \xB0C",document.getElementById("light-intensity-value").innerHTML=e.light_2+" lx"}).catch(e=>console.error("Error fetching sensor data:",e))}setInterval(r,1e3),r()});})();
