(()=>{document.addEventListener("DOMContentLoaded",function(){var t=new JustGage({id:"energy-current-gauge",value:0,min:0,max:400,title:"Aktueller Stromverbrauch",label:"Watt pro Stunde"});function n(){fetch("fetch_sensors.php").then(e=>e.json()).then(e=>{console.log("Received data:",e);var r=parseFloat(e.energy_current);console.log("Parsed energy current value:",r),isNaN(r)?console.error("Invalid energy current value:",e.energy_current):t.refresh(r),document.getElementById("energy-total").innerHTML="Heute im Office verbraucht: "+e.energy_total+" KW",document.getElementById("energy-current").innerHTML="Aktueller Stromverbrauch im Office: "+e.energy_current+" Watt pro Stunde"}).catch(e=>console.error("Error fetching sensor data:",e))}setInterval(n,1e3),n()});})();
